import React from "react";
import Comment from "./Comment";

const comments =[
    {
        name: "이인제",
        comment: "안녕하세요, 소플입니다.",
    },
    {
        name: "이성훈",
        comment: "캠킥 ㅋ",
    },
    {
        name: "강민경",
        comment: "저도 리액트 배우고 싶어요",
    },
];


function CommentList(props){
    return (
        <div>
            {comments.map((comment)=>{
                return(
                    <Comment name={comment.name} comment={comment.comment} />
                );
            })}
        </div>
    );
}

export default CommentList;
